package com.example.tarotcards;

import androidx.appcompat.app.AppCompatActivity;

import android.os.Bundle;
import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;
import java.util.ArrayList;

public class CardsTarotActivity extends AppCompatActivity {

    private RecyclerView courseRV;


    private ArrayList<CourseModel> courseModelArrayList;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_cards_tarot);
        courseRV = findViewById(R.id.idRVCourse);


        courseModelArrayList = new ArrayList<>();
        courseModelArrayList.add(new CourseModel("Дурак", "Вы жаждете новых ощущений и необычных ощущений, и жажда эта толкает вас на необдуманные поступки и скоропалительные решения. Это выглядит так, будто вы вдавили педаль газа в пол и наслаждаетесь мельканием картинок за окном, свистом ветра. Сбросьте же скорость! А то и вовсе остановитесь на обочине и осмотритесь. Каждое событие сейчас имеет для вас огромное значение – вокруг сплошные знаки, обратите на них свое внимание.", R.drawable.card0));
        courseModelArrayList.add(new CourseModel("Маг", "Сейчас ваша сила воли позволит вам принять верные решения. Ничего не бойтесь: интуиция подскажет правильный шаг, а самоконтроль поможет не отказаться от него. Вы исключительный дипломат – на переговорах вы бы обыграли самого Уинстона Черчилля. Ваше мастерство и проницательность, самодостаточность и умение все схватывать на лету позволят вам быть на коне. Сейчас вы как никогда способны достичь успеха, так что вперед к новым начинаниям и духовному росту. Вы вполне способны быть лидером – не только благодаря своей осведомленности, но и внутренним, скрытым, что уж там – «магическим» качествам.", R.drawable.card1));
        courseModelArrayList.add(new CourseModel("Жрица", "Мир перед вами ясен, воздух прозрачен, а вы – несказанно мудры и рассудительны. Не позволяя страстям застилать ваш взор и портить слух, вы готовы не только учиться, но и учить других. У вас сейчас период альтруизма и человеколюбия. Ваши интеллектуальные способности будут оценены по достоинству. Возможно, перед вами раскроются секреты и тайны. И еще: знание, которое вы ищете – внутри вас. Не гоняйтесь за солнечными зайчиками, обратите взор внутрь себя и обнаружите то, что так долго искали.", R.drawable.card2));
        courseModelArrayList.add(new CourseModel("Императрица", "Строительство собственной жизни, а то и зачатие новой, ведь Императрица порой символизирует беременность. Впрочем, возможно, что речь идет о плодотворной жизни вообще – скажем, успехах в работе. Кстати, дела могут быть связаны с матерью или сестрой – в общем, одной из близких родственниц. Ключевое слово этой карты в прямом положении – прогресс. Долгая, счастливая и уютная жизнь сулит комфортный быт и игру ума. Того гляди – можно будет не думать о том, где бы найти хлеб насущный, ведь Императрица всегда символизирует роскошь. Какой-то авторитетный человек будет к вам крайне благосклонен.", R.drawable.card3));
        courseModelArrayList.add(new CourseModel("Император", "Если Император выпадает женщине, то почти всегда это означает наличие у нее сильного покровителя – порой даже неизвестного ей самой. Возможно также, что на даму имеет влияние мужчина старше ее и выше по общественному положению. Мужчина же вполне справедливо может отнести значение аркана на свой счет: в ответственный момент он сможет взять инициативу в свои руки и решить вставшую на его пути проблему. Помимо покровительства, Император еще обозначает вполне неплохие доходы.", R.drawable.card4));
        courseModelArrayList.add(new CourseModel("Жрец", "Вы, несомненно, многое знаете, вы достигли некоего положения, но не стоит путать гордость и гордыню, не нужно останавливаться на достигнутом, зацикливаться, это может привести вас к фанатизму.\n" +
                "\n" +
                "Нельзя быть уверенным на все 100% в своей правоте, ведь вопрос можно решить и другими способами. Возможно, что вы можете подсказать ответ человеку, однако ваш поступок должен быть правильным, не поспешным, иначе он может навредить, а не помочь.", R.drawable.card5));
        courseModelArrayList.add(new CourseModel("Влюбленные", "Итак, Влюбленные прогнозируют выбор, который вам придется сделать. Решение будет непростым, но, приняв его, вы перейдете на более высокий уровень в духовном развитии. Если же вы хотите получить ответ на более приземленные вопросы, то карта сообщает вам о затруднительном положении, в которое вы попадете. Однако выход есть: на него укажут карты, которые лежат рядом. Если же вы спрашиваете о личном, то безусловно, Влюбленные говорят и о присутствии в вашей жизни серьезной сердечной привязанности. Узнать, взаимно ли чувство и какой будет исход романа можно благодаря Младшим Арканам, лежащим в этом раскладе рядом с Влюбленными.", R.drawable.card6));
        courseModelArrayList.add(new CourseModel("Колесница", "Вы герой, который в состоянии доказать Року свою силу, поскольку можете распорядиться событиями по собственному усмотрению, не ожидая его подсказок. Возможно, что вы только готовитесь к серьезному делу. Тогда не мешкайте и беритесь за него всерьез! Уделите внимание тем картам, которые окружают Колесницу, ибо они подскажут, какое именно событие вас ожидает. Ведь именно оно поможет вам оказаться не «на щите», а «со щитом».", R.drawable.card7));
        courseModelArrayList.add(new CourseModel("Сила", "Вас можно поздравить. Правьте своей жизнью так, как вам угодно. Вы маг и волшебник, которому подвластны превратности судьбы. Но при одном условии – если не хотите повредить окружающим. Одна нечистая мысль – будь то зависть или злость, ревность или неприязнь – способны разрушить вашу силу.\n" +
                "\n" +
                "Но ваша яркость и жизнелюбие, притягивая людей, притягивает как друзей, так и врагов. Так что остерегайтесь завистников. И ни на минуту не переставайте работать над собой: в вас слишком много энергии, ее необходимо направить во благо. По другим картам, окружающим Силу в раскладе, вы поймете, что правильно двигались до сегодняшнего момента, и как вы поведете себя дальше – зависит только от вас.", R.drawable.card8));
        courseModelArrayList.add(new CourseModel("Отшельник", "Судьба к вам будет благосклонна, ведь вы так рассудительны. Эмоции – ваши друзья, а не враги, вы благополучно обуздали их. А такие качества помогают обращать даже самых заклятых врагов в преданных друзей. Тем не менее, говорит Отшельник, вы должны быть очень осторожны в своих действиях. Если лишь на минуту выпустите вожжи – вместо победителя окажетесь побежденным.", R.drawable.card9));
        courseModelArrayList.add(new CourseModel("Колесо Фортуны", "Вы рискуете весело и бесшабашно, вы смелы, а не трясетесь от страха, и полагаетесь на свое везение. У вас чудесный период жизнелюбия, вы легкий и яркий – люди тянутся к вам сами. Вы бы может быть даже сыграли в казино, но вам совершенно несвойственны глупые поступки – а ведь это глупо полагаться только на везение, без всякого расчета. Ко всем своим качествам человека-фейерверка – вы благоразумны и расчетливы.", R.drawable.card10));
        courseModelArrayList.add(new CourseModel("Равновесие","Знак стабильности и уравновешенности. Вы из числа тех немногих, кто может найти компромисс даже в самой сложной ситуации. И это будет не уступка сильному, а решение, которое устроит обе стороны. Вы сейчас как никогда адекватны и трезвы, так что решение самых головоломных задачек вам по зубам. Похоже, помощь свыше вам обеспечена. Правосудие на вашей стороне!", R.drawable.card11));
        courseModelArrayList.add(new CourseModel("Повешанный",  "Глубина ваших чувств и исканий несомненна. Вы готовы отдавать самое лучшее взамен на счастье ближних. Но при этом вы осторожны и предусмотрительны. Скажем, если вы пойдете сдавать кровь для пострадавших, то обязательно проверите, одноразовые ли иглы использует медсестра. Вы ищете себя в жизни постоянно, и скоро удача улыбнется вам. Правда, как уже говорилось, путем серьезных неожиданных потерь или сознательных жертв. Ваш жизненный путь пришел к тому моменту, где необходимо потерять, чтобы идти дальше. Не цепляйтесь за то, что уже отмерло, ибо так жертва будет еще серьезней и болезненней.", R.drawable.card12));
        courseModelArrayList.add(new CourseModel("Смерть", "Вы – безусловно сильный и яркий человек, вы стремитесь к переменам, которые полностью изменят всю вашу последующую жизнь. Вы идете навстречу неизвестному с открытыми глазами, не пугаясь того, что ждет впереди. И вы правы: перемена это не гильотина, а свежий воздух. Однако внимательно посмотрите на окружение Аркана Смерть. Эти карты подскажут вам, как действовать дальше.", R.drawable.card13));
        courseModelArrayList.add(new CourseModel("Умеренность", "Вы сговорчивы, уравновешенны и спокойны. Вас ничто не может вышибить из седла – ни чужие эмоции, ни мелкие незначительные происшествия. А крупных, сообщает Уравновешенность, пока и не будет. Вы ясны, как солнечное летнее утро, и жизнь ваша тоже тиха и спокойна. Считайте, что у вас отпуск от житейских бурь. Конечно, важно и то, что вам сейчас бессмысленно надеяться на форсирование событий, и если у вас есть какая-то мечта, то повремените с ее исполнением. Уютное болото Уравновешенности просто не пустит вас. Терпение – одна из самых главных человеческих добродетелей. Отдохните, наберитесь сил, и скоро штиль сменится попутным ветром.", R.drawable.card14));
        courseModelArrayList.add(new CourseModel("Дьявол", "Это предупреждение судьбы. Похоже, что вы стремитесь к ложной цели. Ваша мечта – совсем не то, что вам по-настоящему необходимо. Что заставляет вас так себя вести? Жажда блеснуть? Ложное чувство собственной значимости? Попробуйте взвесить все, и если поймете, что цель и вправду не ваша – откажитесь от нее, пока не поздно. Иначе в следующий раз Диавол может выпасть вам и перевернутым.", R.drawable.card15));
        courseModelArrayList.add(new CourseModel("Башня", "Вы сильно обеспокоены и нервничаете. Портите отношения с окружающими, любой пустяк выводит вас из себя. Более того, вы склонны обвинять во всем не себя, а тех, кто пострадал от вашей несдержанности. Ругань и скандалы – вот та атмосфера, в которой вам сейчас уютно. И чем дальше – тем глубже вы влезаете в атмосферу склок и дрязг. Это кризис вашей обыденной жизни. Вас не устраивает абсолютно все, поэтому-то вы на взводе. Но перемены обязательно произойдут, но не снаружи, а внутри вас. Старайтесь держать себя в руках и терпите: старое уже сметено, растут новые взгляды на жизнь!", R.drawable.card16));
        courseModelArrayList.add(new CourseModel("Звезда", "Присущее вам обаяние и не исчезающее хорошее настроение поможет воплощать идеи в жизнь. Тем более что плохих идей у вас нет. Звезда позволяет надеяться на положительный исход непростой ситуации. Карта говорит: ни в коем случае не вешайте носа. Уныние может только повредить ситуации. Здоровье на протяжении довольно долгого времени также будет отменным!", R.drawable.card17));
        courseModelArrayList.add(new CourseModel("Луна", "Вы боретесь с собой и своими страхами, и потому не боитесь взглянуть в глаза опасности. То, что сейчас на поверхности, обычно спрятано внутри вас, но силы вполне достаточно, чтоб побороть темноту внутри. Интуиция работает как никогда – это все лунное сумеречное влияние, и оно вам на пользу. Скорее всего, из этой схватки с самим собой вы выйдете победителем. Более того – обогащенным новыми знаниями.", R.drawable.card18));
        courseModelArrayList.add(new CourseModel("Солнце", "Солнечный период жизни, светило явно благосклонно к вам. Непростая ситуация решится с его помощью удивительно легко, и все мечты осуществятся. Солнце обещает вам спокойствие и счастье в крепком романтическом союзе. Более того – возможно пополнение семейства!", R.drawable.card19));
        courseModelArrayList.add(new CourseModel("Возрождение", "Ситуация благоприятна для всякого рода творчества. Ваша фантазия безгранична, и силы для воплощения ее плодов – тоже. Творите – вас оценят по достоинству. Более того, если вы будете достаточно щедры – не столько в материальном, сколько в умственном и душевном смысле – то получите нескольких преданных друзей. Желание исполнится – вы славно поработали для этого! А еще вас ждет сюрприз, причем обязательно приятный!", R.drawable.card20));
        courseModelArrayList.add(new CourseModel("Мир", "Вы пережили период сомнений и оказались на широкой дороге, разветвляющейся на множество путей, причем каждый из них – верный. Вы мудры, у вас прекрасная интуиция, решайте скорей – и вперед! Мир предвещает безусловную победу в делах, ведь все вокруг абсолютно совершенно, а вы уверены в своих деяниях. Мир – это благоприятный ответ на любой вопрос, он говорит вам «да», потому что вы ведете себя в соответствии с законами мира.", R.drawable.card21));


        CourseAdapter courseAdapter = new CourseAdapter(this, courseModelArrayList);


        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false);

        courseRV.setLayoutManager(linearLayoutManager);
        courseRV.setAdapter(courseAdapter);
    }
}